#![cfg(test)]

use super::*;
use ethabi::ParamType;
use soroban_sdk::{
    testutils::{Address as _, Logs},
    Env,
};

mod orally_verifyer {
    soroban_sdk::contractimport!(
        file = "../../target/wasm32-unknown-unknown/release/orally_verifyer.wasm"
    );
}

extern crate std;

const TEST_REPORTER: &'static str = "0x61E9658dFE7c620E96ae41f97b89B079Ef7ECd1A";
const TEST_GET_XRC_DATA_BYTES: &'static str = "";
const TEST_GET_XRC_DATA_BYTES_NOT_A_REPORTER: &'static str = "";

fn init_orally_verifyer<'a>(env: &'a Env, owner: &'a Address) -> Address {
    let contract_id = env.register_contract_wasm(None, orally_verifyer::WASM);
    let client = OrallyVerifyerClient::new(&env, &contract_id);

    let test_reporter = String::from_str(&env, TEST_REPORTER);

    client.init(&owner);
    client.add_reporter(&test_reporter);

    contract_id
}

fn init_contract<'a>(env: &'a Env, owner: &'a Address) -> OrallyVerifyerExampleClient<'a> {
    let orally_verifyer_address = init_orally_verifyer(env, owner);
    let contract_id = env.register_contract(None, OrallyVerifyerExample);
    let client = OrallyVerifyerExampleClient::new(&env, &contract_id);
    client.init(&owner, &orally_verifyer_address);

    client
}

#[test]
fn test() {
    let env = Env::default();
    env.mock_all_auths();

    let owner = Address::generate(&env);

    let client = init_contract(&env, &owner);

    let hex = hex::decode(&TEST_GET_XRC_DATA_BYTES[2..]).unwrap();

    let bytes = Bytes::from_slice(&env, &hex);

    let mut vec = ethabi::decode(
        &[ParamType::Bytes, ParamType::Bytes, ParamType::Bytes],
        &bytes.to_alloc_vec(),
    )
    .unwrap();

    let _signature_bytes = vec.pop().unwrap().into_bytes().unwrap();
    let _meta_bytes = vec.pop().unwrap().into_bytes().unwrap();
    let generic_bytes = vec.pop().unwrap().into_bytes().unwrap();

    std::println!("{}", hex::encode(&generic_bytes));
    std::println!("{}", hex::encode(&_meta_bytes));
    std::println!("{}", hex::encode(&_signature_bytes));

    let verify_generic_result = client.update_feed(&bytes);
    assert_eq!(
        verify_generic_result,
        Bytes::from_slice(&env, &generic_bytes)
    );

    let logs = env.logs().all();
    std::println!("{}", logs.join("\n"));
}

#[test]
#[should_panic]

